#! /usr/bin/env node
const readline = require('readline').createInterface({
    input: process.stdin,
    output: process.stdout,
    prompt: 'enter command > ',
});
readline.prompt();
readline.on('line', line =>{
    switch(line.trim()){
        case 'list vegan food':
            console.log('vegan food list');
            break;
        case 'log':
            readline.question(`What you want to log today? `, item => {
                const { data } = await axios.get(`http://localhost:3001/food`);
                const it = data[Symbol.iterator]();
                let position  = it.next();
                while(!position.done){
                    const food = position.value.name;
                    if ( food === item ){
                        console.log(`${item} has ${position.value.name} calories`); 
                    }
                    position = it.next();
                }
                console.log(item);
                readline.close();
            });
    }
})